mapper.select:
SELECT * FROM TB_EVENT_INFO WHERE DCB = #{dcb}
AND EVENT_NAME LIKE CONCAT('%', #{eventName}, '%')
ORDER BY STR_TO_DATE(REG_DT, '%Y-%m-%d') DESC

mapper.delete:
DELETE FROM TB_EVENT_INFO
WHERE DCB = #{dcb}
AND EVENT_NAME = #{eventName}

mapper.select:
SELECT COUNT > 0
FROM TB_EVENT_INFO
WHERE DCB = #{dcb}
AND EVENT_NAME = #{eventName}
