mapper.select:
SELECT COUNT(*) > 0
FROM MEMBER
WHERE MEMBER_NAME = #{memberName}

SELECT COUNT(*) > 0
FROM MEMBER
WHERE CTN = #{ctn}

SELECT COUNT(*) > 0
FROM MEMBER
WHERE EMAIL = #{signUpDto.email}

mapper.update:
UPDATE MEMBER
SET LAST_CONNECTED_DATETIME = DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s')
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT MEMBER_ID, POSITION, MEMBER_NAME, NAME, CTN, EMAIL, LAST_CONNECTED_DATETIME
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{updateMemberRequestDto.password},
    CTN = #{updateMemberRequestDto.ctn},
    EMAIL = #{updateMemberRequestDto.email}
WHERE MEMBER_ID = #{updateMemberRequestDto.memberId}
