mapper.select:
SELECT SUBSTR(A.YEAR_MONTH, 1, 4) || '-' || SUBSTR(A.YEAR_MONTH, 5, 2) AS yearMonth, B.CODE_VALUE AS fileType, A.FILE_CD AS fileCode, A.FILE_NAME AS fileName, A.FILE_PATH AS filePath, C.CODE_VALUE AS result, TO_CHAR(CREATE_DT, 'YYYY-MM-DD') AS createDt, TO_CHAR(UPDATE_DT, 'YYYY-MM-DD') AS updateDt FROM TB_CREATE_FILE_INFO A LEFT JOIN TB_CODE B ON B.CODE_GUBUN = 'FILE_NAME' AND A.FILE_TYPE = B.CODE LEFT JOIN TB_CODE C ON C.CODE_GUBUN = 'CR_RESULT' AND A.RESULT = C.CODE INNER JOIN TB_CODE D ON D.CODE_GUBUN = 'DOWN_YN' AND A.FILE_TYPE = D.CODE AND D.CODE_VALUE = 'Y' WHERE A.YEAR_MONTH = #{yearMonth}
AND B.CODE_VALUE = #{fileType}
ORDER BY C.CODE_GUBUN, C.CODE

SELECT A.FILE_PATH || '/' || A.FILE_NAME AS fullPath FROM TB_CREATE_FILE_INFO A LEFT JOIN TB_CODE B ON B.CODE_GUBUN = 'FILE_NAME' AND A.FILE_TYPE = B.CODE WHERE 1 = 1
AND A.FILE_NAME = #{fileName}
AND B.CODE_VALUE = #{fileType}
AND A.YEAR_MONTH = REPLACE(#{yearMonth}, '-', '')

SELECT TRANSACTION_TYPE, REVS_CATEGORY, SUM(CNT) AS transactionCnt, SUM(ITEM_PRICE_SUM) AS itemPriceSum, SUM(TAX_SUM) AS taxSum, SUM(TOTAL_AMOUNT_SUM) AS totalAmountSum, SUM(REVS_IN_INVOICED_CURRENCY_SUM) AS revsInInvoicedCurrencySum FROM TB_MONTHLY_INVOICE_SUM WHERE 1 = 1
AND PAYMENT_TYPE = #{paymentType}
AND YEAR = #{year} AND MONTH = #{month} AND TRANSACTION_TYPE = 'B' GROUP BY TRANSACTION_TYPE, REVS_CATEGORY

SELECT TRANSACTION_TYPE, REVS_CATEGORY, SUM(CNT) AS transactionCnt, SUM(ITEM_PRICE_SUM) AS itemPriceSum, SUM(TAX_SUM) AS taxSum, SUM(TOTAL_AMOUNT_SUM) AS totalAmountSum, SUM(REVS_IN_INVOICED_CURRENCY_SUM) AS revsInInvoicedCurrencySum FROM TB_MONTHLY_INVOICE_SUM WHERE 1 = 1
AND PAYMENT_TYPE = #{paymentType}
AND YEAR = #{year} AND MONTH = #{month} AND TRANSACTION_TYPE = 'R' GROUP BY TRANSACTION_TYPE, REVS_CATEGORY
