mapper.delete:
DELETE FROM TB_VOC_HISTORY
WHERE 1 = 1

    AND VOC_ID = #{id}

mapper.insert:
INSERT INTO TB_VOC_HISTORY(REG_DATE, CTN, DCB, CONTENT, WRITER
, DIVISION_1
, DIVISION_2
, DIVISION_3
, ANSWER_1
, ANSWER_2
, ANSWER_3)
VALUES (SYSDATE, #{ctn}, #{dcb}, #{content}
, #{division1}
, #{division2}
, #{division3}
, #{answer1}
, #{answer2}
, #{answer3}
)

mapper.update:
UPDATE TB_VOC_HISTORY

    ANSWER_1 = #{answer1},
    ANSWER_2 = #{answer2},
    ANSWER_3 = #{answer3}

WHERE VOC_ID = #{vocId}
